import { useEffect, useState } from "react";
import axios from "axios";

export default function AdminDashboard() {

  const [metrics, setMetrics] = useState(null);
  const [grievances, setGrievances] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    const token = localStorage.getItem("token");

    try {
      const metricsRes = await axios.get(
        "http://localhost:8080/api/admin/dashboard/metrics",
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const grievancesRes = await axios.get(
        "http://localhost:8080/api/admin/grievances",
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMetrics(metricsRes.data);
      setGrievances(grievancesRes.data);

    } catch {
      alert("Failed to load admin dashboard");
    } finally {
      setLoading(false);
    }
  };

  /* ========= STYLES ========= */

  const page = {
    minHeight:"100vh",
    padding:"40px",
    fontFamily:"Inter, sans-serif",
    background:
      "radial-gradient(circle at 20% 20%, #00F0FF30, transparent 40%), radial-gradient(circle at 80% 80%, #ff00cc30, transparent 40%), linear-gradient(120deg,#0f0c29,#302b63,#24243e)",
    color:"#fff"
  };

  const glass = {
    backdropFilter:"blur(18px)",
    background:"rgba(255,255,255,0.05)",
    border:"1px solid rgba(255,255,255,0.15)",
    borderRadius:"18px",
    boxShadow:"0 20px 60px rgba(0,0,0,0.6)"
  };

  const grid = {
    display:"grid",
    gridTemplateColumns:"repeat(auto-fit,minmax(200px,1fr))",
    gap:"18px",
    marginTop:"30px"
  };

  const metricCard = accent => ({
    ...glass,
    padding:"20px",
    borderLeft:`5px solid ${accent}`,
    transition:"0.3s"
  });

  const tableWrap = {
    ...glass,
    padding:"25px",
    marginTop:"40px"
  };

  const table = {
    width:"100%",
    borderCollapse:"collapse"
  };

  const th = {
    padding:"14px",
    textAlign:"left",
    fontSize:"13px",
    opacity:0.7,
    borderBottom:"1px solid rgba(255,255,255,0.2)"
  };

  const td = {
    padding:"14px",
    fontSize:"14px",
    borderBottom:"1px solid rgba(255,255,255,0.08)"
  };

  const hoverRow = e =>
    e.currentTarget.style.background="rgba(255,255,255,0.05)";

  const leaveRow = e =>
    e.currentTarget.style.background="transparent";

  const statusStyle = s => {
    switch (s) {
      case "PENDING":
        return { background:"#78350f", color:"#fbbf24" };
      case "IN_PROGRESS":
        return { background:"#1e3a8a", color:"#60a5fa" };
      case "RESOLVED":
        return { background:"#14532d", color:"#4ade80" };
      case "CANCELLED":
        return { background:"#7f1d1d", color:"#f87171" };
      default:
        return {};
    }
  };

  const actionBtn = color => ({
    background:color,
    color:"#000",
    border:"none",
    padding:"7px 12px",
    borderRadius:"8px",
    cursor:"pointer",
    fontWeight:600
  });

  /* ========= LOADING ========= */

  if (loading) {
    return (
      <div style={page}>
        <div style={{...glass,padding:"30px"}}>
          <h3>Loading Admin Dashboard...</h3>
        </div>
      </div>
    );
  }

  /* ========= UI ========= */

  return (
    <div style={page}>

      <h1>Admin Control Panel</h1>
      <p style={{opacity:0.7}}>
        Monitor system grievances and workflow
      </p>

      {/* METRICS */}
      <div style={grid}>
        <Metric title="Total" value={metrics.total} accent="#6366f1" />
        <Metric title="Pending" value={metrics.pending} accent="#f59e0b" />
        <Metric title="In Progress" value={metrics.inProgress} accent="#0ea5e9" />
        <Metric title="Resolved" value={metrics.resolved} accent="#22c55e" />
        <Metric title="Cancelled" value={metrics.cancelled} accent="#ef4444" />
      </div>

      {/* TABLE */}
      <div style={tableWrap}>
        <h3>All Grievances</h3>

        <div style={{overflowX:"auto"}}>
          <table style={table}>
            <thead>
              <tr>
                <th style={th}>ID</th>
                <th style={th}>User</th>
                <th style={th}>Department</th>
                <th style={th}>Subject</th>
                <th style={th}>Location</th>
                <th style={th}>Status</th>
                <th style={th}>Created</th>
                <th style={th}>Action</th>
              </tr>
            </thead>

            <tbody>
              {grievances.map(g => (
                <tr
                  key={g.id}
                  onMouseEnter={hoverRow}
                  onMouseLeave={leaveRow}
                >
                  <td style={td}>{g.id}</td>
                  <td style={td}>{g.userEmail}</td>
                  <td style={td}>{g.department}</td>
                  <td style={td}>{g.subject}</td>
                  <td style={td}>{g.location}</td>

                  <td style={td}>
                    <span style={{
                      padding:"6px 12px",
                      borderRadius:"12px",
                      fontWeight:600,
                      fontSize:"12px",
                      ...statusStyle(g.status)
                    }}>
                      {g.status}
                    </span>
                  </td>

                  <td style={td}>
                    {new Date(g.createdAt).toLocaleString()}
                  </td>

                  <td style={td}>
                    {g.status === "PENDING" && (
                      <button style={actionBtn("#0ea5e9")}>
                        In Progress
                      </button>
                    )}
                    {g.status === "IN_PROGRESS" && (
                      <button style={actionBtn("#22c55e")}>
                        Resolve
                      </button>
                    )}
                  </td>

                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

    </div>
  );
}

/* ========= METRIC COMPONENT ========= */

function Metric({ title, value, accent }) {
  return (
    <div style={{
      backdropFilter:"blur(18px)",
      background:"rgba(255,255,255,0.05)",
      border:"1px solid rgba(255,255,255,0.15)",
      borderRadius:"18px",
      padding:"20px",
      borderLeft:`5px solid ${accent}`
    }}>
      <p style={{opacity:0.7, margin:0}}>{title}</p>
      <h2 style={{margin:"6px 0 0"}}>{value}</h2>
    </div>
  );
}
